import { createSlice } from '@reduxjs/toolkit';
import { fetchThoughts } from '../actions/thoughtActions';

const thoughtsSlice = createSlice({
  name: 'thoughts',
  initialState: [],
  reducers: {
    setThoughts: (state, action) => action.payload,
    addThought: (state, action) => {
      state.push(action.payload);
    },
  },
  extraReducers: {
    [fetchThoughts.fulfilled]: (state, action) => action.payload,
  },
});

export const { setThoughts, addThought } = thoughtsSlice.actions;

export default thoughtsSlice.reducer;
