import axios from 'axios';

// Action types
export const FETCH_ITEMS = 'FETCH_ITEMS';
export const DELETE_ITEM = 'DELETE_ITEM';
export const EDIT_ITEM = 'EDIT_ITEM';

// Action creators
export const fetchItems = (itemType) => async (dispatch) => {
  const response = await axios.get(`http://localhost:5000/${itemType}`);
  dispatch({ type: FETCH_ITEMS, payload: response.data, itemType });
};

export const deleteItem = (itemType, id) => async (dispatch) => {
  await axios.delete(`http://localhost:5000/${itemType}/${id}`);
  dispatch({ type: DELETE_ITEM, payload: id, itemType });
};

export const editItem = (itemType, item) => async (dispatch) => {
  const response = await axios.put(`http://localhost:5000/${itemType}/${item.id}`, item);
  dispatch({ type: EDIT_ITEM, payload: response.data, itemType });
};
