import axios from 'axios';

// Action types
export const CREATE_PROJECT = 'CREATE_PROJECT';
export const FETCH_PROJECTS = 'FETCH_PROJECTS';
export const DELETE_PROJECT = 'DELETE_PROJECT';
export const EDIT_PROJECT = 'EDIT_PROJECT';

// Action creators
export const createProject = (project) => async (dispatch) => {
  const response = await axios.post('http://localhost:5000/projects', project);
  dispatch({ type: CREATE_PROJECT, payload: response.data });
};

export const fetchProjects = () => async (dispatch) => {
  const response = await axios.get('http://localhost:5000/projects');
  dispatch({ type: FETCH_PROJECTS, payload: response.data });
};

export const deleteProject = (id) => async (dispatch) => {
  await axios.delete(`http://localhost:5000/projects/${id}`);
  dispatch({ type: DELETE_PROJECT, payload: id });
};

export const editProject = (project) => async (dispatch) => {
  const response = await axios.put(`http://localhost:5000/projects/${project.id}`, project);
  dispatch({ type: EDIT_PROJECT, payload: response.data });
};
