import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  sidebarInfo: {
    id: null,
    fullName: '',
    description: '',
    currentOccupation: '',
    image: ''
  },
  error: null
};

const sidebarSlice = createSlice({
  name: 'sidebar',
  initialState,
  reducers: {
    setSidebarInfo: (state, action) => {
      state.sidebarInfo = action.payload;
      state.error = null;
    },
    setSidebarInfoError: (state, action) => {
      state.error = action.payload;
    }
  }
});

export const { setSidebarInfo, setSidebarInfoError } = sidebarSlice.actions;
export default sidebarSlice.reducer;
