import { createSlice } from '@reduxjs/toolkit';

const thoughtSlice = createSlice({
  name: 'thoughts',
  initialState: [],
  reducers: {
    setThoughts: (state, action) => action.payload,
    addThought: (state, action) => {
      state.push(action.payload);
    },
    deleteThought: (state, action) => {
      return state.filter(thought => thought.id !== action.payload);
    },
    editThought: (state, action) => {
      return state.map(thought => thought.id === action.payload.id ? action.payload : thought);
    }
  }
});

export const { setThoughts, addThought, deleteThought, editThought } = thoughtSlice.actions;
export default thoughtSlice.reducer;
