import { createSlice } from '@reduxjs/toolkit';
import { fetchSidebarInfo } from '../actions/SidebarActions';

const sidebarSlice = createSlice({
  name: 'sidebar',
  initialState: {
    sidebarInfo: null,
    error: null,
  },
  reducers: {
    setSidebarInfo: (state, action) => {
      state.sidebarInfo = action.payload;
    },
    setSidebarError: (state, action) => {
      state.error = action.payload;
    },
  },
  extraReducers: {
    [fetchSidebarInfo.fulfilled]: (state, action) => {
      state.sidebarInfo = action.payload;
      state.error = null;
    },
    [fetchSidebarInfo.rejected]: (state, action) => {
      state.sidebarInfo = null;
      state.error = action.error.message;
    },
  },
});

export const { setSidebarInfo, setSidebarError } = sidebarSlice.actions;

export default sidebarSlice.reducer;
