import axios from 'axios';
import { setExperiences, addExperience } from '../reducers/experienceReducer';

export const fetchExperiences = () => async (dispatch) => {
  const response = await axios.get('http://localhost:5000/experiences');
  dispatch(setExperiences(response.data));
};

export const createExperience = (experience) => async (dispatch) => {
  const response = await axios.post('http://localhost:5000/experiences', experience);
  dispatch(addExperience(response.data));
};
