import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addProject, fetchProjects } from '../actions/projectActions';
import { addExperience, fetchExperiences, deleteExperience, updateExperience } from '../actions/experienceActions';
import { addThought, fetchThoughts, deleteThought, updateThought } from '../actions/thoughtActions';
import { fetchSidebarInfo, updateSidebarInfo, addTool, fetchTools } from '../actions/SidebarActions';
import { useAuth } from '../context/AuthContext';
import './AdminPage.css';

const AdminPage = () => {
  const dispatch = useDispatch();
  const { logout } = useAuth();
  const thoughts = useSelector((state) => state.thoughts);
  const experiences = useSelector((state) => state.experiences);
  const sidebarInfo = useSelector((state) => state.sidebar.sidebarInfo);

  const [project, setProject] = useState({ title: '', description: '', photos: [], link: '' });
  const [experience, setExperience] = useState({ title: '', date: '', description: '', photo: '', link: '' });
  const [editingExperience, setEditingExperience] = useState(null);
  const [editingThought, setEditingThought] = useState(null);
  const [thoughtText, setThoughtText] = useState('');
  const [sidebar, setSidebar] = useState({ fullName: '', currentPosition: '', description: '', photo: '' });
  const [tool, setTool] = useState({ name: '', logoUrl: '' });
  const [imageInput, setImageInput] = useState('');

  useEffect(() => {
    dispatch(fetchProjects());
    dispatch(fetchExperiences());
    dispatch(fetchThoughts());
    dispatch(fetchSidebarInfo());
    dispatch(fetchTools());
  }, [dispatch]);

  useEffect(() => {
    if (sidebarInfo) {
      setSidebar(sidebarInfo);
    }
  }, [sidebarInfo]);

  const handleProjectSubmit = (e) => {
    e.preventDefault();
    dispatch(addProject(project)).then(() => dispatch(fetchProjects()));
    setProject({ title: '', description: '', photos: [], link: '' });
    setImageInput('');
  };

  const handleExperienceSubmit = (e) => {
    e.preventDefault();
    if (editingExperience) {
      dispatch(updateExperience({ id: editingExperience.id, ...experience })).then(() => {
        setEditingExperience(null);
        setExperience({ title: '', date: '', description: '', photo: '', link: '' });
        dispatch(fetchExperiences());
      });
    } else {
      dispatch(addExperience(experience)).then(() => {
        setExperience({ title: '', date: '', description: '', photo: '', link: '' });
        dispatch(fetchExperiences());
      });
    }
  };

  const handleEditExperience = (exp) => {
    setEditingExperience(exp);
    setExperience({
      title: exp.title,
      date: exp.date,
      description: exp.description,
      photo: exp.photo,
      link: exp.link,
    });
  };

  const handleCancelExperienceEdit = () => {
    setEditingExperience(null);
    setExperience({ title: '', date: '', description: '', photo: '', link: '' });
  };

  const handleThoughtSubmit = (e) => {
    if (e) {
      e.preventDefault();
    }

    if (editingThought) {
      dispatch(updateThought({ id: editingThought.id, text: thoughtText })).then(() => {
        setEditingThought(null);
        setThoughtText('');
        dispatch(fetchThoughts());
      });
    } else {
      dispatch(addThought({ text: thoughtText })).then(() => {
        setThoughtText('');
        dispatch(fetchThoughts());
      });
    }
  };

  const handleEdit = (thought) => {
    setEditingThought(thought);
    setThoughtText(thought.text);
  };

  const handleCancelEdit = () => {
    setEditingThought(null);
    setThoughtText('');
  };

  const handleThoughtChange = (e) => {
    setThoughtText(e.target.value);
  };

  const handleDelete = (id, type) => {
    if (type === 'thought') {
      dispatch(deleteThought(id)).then(() => dispatch(fetchThoughts()));
    } else if (type === 'experience') {
      dispatch(deleteExperience(id)).then(() => dispatch(fetchExperiences()));
    }
  };

  const handleSidebarSubmit = (e) => {
    e.preventDefault();
    dispatch(updateSidebarInfo(sidebar));
    setImageInput('');
  };

  const handleToolSubmit = (e) => {
    e.preventDefault();
    dispatch(addTool(tool)).then(() => dispatch(fetchTools()));
    setTool({ name: '', logoUrl: '' });
  };

  const handleImageInputChange = (e, setFunction, stateField) => {
    const value = e.target.value;
    setImageInput(value);

    if (value.startsWith('http://') || value.startsWith('https://')) {
      setFunction(prev => ({ ...prev, [stateField]: value }));
    } else if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      const reader = new FileReader();
      reader.onloadend = () => {
        setFunction(prev => ({
          ...prev,
          [stateField]: reader.result,
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="admin-page">
      <div className="header">
        <h1>Admin Panel</h1>
        <button className="logout-button" onClick={logout}>Sign Out</button>
      </div>

      <div className="content">
        {/* Project Form */}
        <form onSubmit={handleProjectSubmit}>
          <h2>Add Project</h2>
          <input
            type="text"
            placeholder="Project Title"
            value={project.title}
            onChange={(e) => setProject({ ...project, title: e.target.value })}
          />
          <textarea
            placeholder="Project Description"
            value={project.description}
            onChange={(e) => setProject({ ...project, description: e.target.value })}
          ></textarea>
          <input
            type="text"
            placeholder="Project Link"
            value={project.link}
            onChange={(e) => setProject({ ...project, link: e.target.value })}
          />
          <input
            type="text"
            placeholder="Project Image URL or select a file"
            value={imageInput}
            onChange={(e) => handleImageInputChange(e, setProject, 'photos')}
            onClick={() => setImageInput('')} 
          />
          <input
            type="file"
            onChange={(e) => handleImageInputChange(e, setProject, 'photos')}
          />
          <button type="submit">Add Project</button>
        </form>

        {/* Experience Form */}
        <form onSubmit={handleExperienceSubmit}>
          <h2>{editingExperience ? 'Edit Experience' : 'Add Experience'}</h2>
          <input
            type="text"
            placeholder="Experience Title"
            value={experience.title}
            onChange={(e) => setExperience({ ...experience, title: e.target.value })}
          />
          <input
            type="text"
            placeholder="Date (e.g., January 2020)"
            value={experience.date}
            onChange={(e) => setExperience({ ...experience, date: e.target.value })}
          />
          <textarea
            placeholder="Experience Description"
            value={experience.description}
            onChange={(e) => setExperience({ ...experience, description: e.target.value })}
          ></textarea>
          <input
            type="text"
            placeholder="Experience Image URL"
            value={experience.photo}
            onChange={(e) => setExperience({ ...experience, photo: e.target.value })}
          />
          <input
            type="text"
            placeholder="Experience Link"
            value={experience.link}
            onChange={(e) => setExperience({ ...experience, link: e.target.value })}
          />
          <button type="submit">{editingExperience ? 'Update Experience' : 'Add Experience'}</button>
          {editingExperience && (
            <button onClick={handleCancelExperienceEdit} type="button">
              Cancel Edit
            </button>
          )}
        </form>

        {/* Thoughts, Sidebar, and Tools Management */}
        {/* Keep the existing forms and structures here */}

        {/* Manage Experiences Table */}
        <h2>Manage Experiences</h2>
        <div className="experiences-table-container">
          <table className="experiences-table">
            <thead>
              <tr>
                <th>Title</th>
                <th>Date</th>
                <th>Description</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {experiences.map((exp) => (
                <tr key={exp.id}>
                  <td>{exp.title}</td>
                  <td>{exp.date}</td>
                  <td>{exp.description}</td>
                  <td>
                    <button onClick={() => handleEditExperience(exp)}>Edit</button>
                    <button onClick={() => handleDelete(exp.id, 'experience')}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
