import { FETCH_THOUGHTS, CREATE_THOUGHT, DELETE_THOUGHT, EDIT_THOUGHT } from '../actions/thoughtActions';

const initialState = [];

const thoughtReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_THOUGHTS:
      return action.payload;
    case CREATE_THOUGHT:
      return [...state, action.payload];
    case DELETE_THOUGHT:
      return state.filter((thought) => thought.id !== action.payload);
    case EDIT_THOUGHT:
      return state.map((thought) =>
        thought.id === action.payload.id ? action.payload : thought
      );
    default:
      return state;
  }
};

export default thoughtReducer;
