import { combineReducers } from 'redux';
import adminReducer from './adminReducer';
import sidebarReducer from './SidebarReducer'; // If you have a separate sidebar reducer

const rootReducer = combineReducers({
  admin: adminReducer,
  sidebar: sidebarReducer, // If you have a separate sidebar reducer
  // Add other reducers here
});

export default rootReducer;
