import { FETCH_ITEMS, DELETE_ITEM, EDIT_ITEM } from '../actions/adminActions';

const initialState = {
  projects: [],
  experiences: [],
  thoughts: [],
  sidebarInfo: {
    id: null,
    fullName: '',
    description: '',
    currentOccupation: '',
    image: ''
  }
};

const adminReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_ITEMS:
      return { ...state, [action.itemType]: action.payload };
    case DELETE_ITEM:
      return {
        ...state,
        [action.itemType]: state[action.itemType].filter((item) => item.id !== action.payload)
      };
    case EDIT_ITEM:
      return {
        ...state,
        [action.itemType]: state[action.itemType].map((item) =>
          item.id === action.payload.id ? action.payload : item
        )
      };
    default:
      return state;
  }
};

export default adminReducer;
