      e.preventDefault();
    }

    if (editingThought) {
      // If editing, update the thought
      dispatch(updateThought({ id: editingThought.id, text: thoughtText })).then(() => {
        setEditingThought(null);
        setThoughtText('');
        dispatch(fetchThoughts()); // Refetch thoughts after update
      });
