          <textarea
            placeholder="Experience Description"
            value={experienceData.description}
            onChange={(e) => setExperienceData({ ...experienceData, description: e.target.value })}
          ></textarea>
          <input
            type="text"
            placeholder="Experience Image URL"
            value={experienceData.photo}
            onChange={(e) => setExperienceData({ ...experienceData, photo: e.target.value })}
          />
          <input
            type="text"
            placeholder="Experience Link"
            value={experienceData.link}
            onChange={(e) => setExperienceData({ ...experienceData, link: e.target.value })}
          />
          <button type="submit">{editingExperience ? 'Update Experience' : 'Add Experience'}</button>
          {editingExperience && (
            <button onClick={handleCancelExperienceEdit} type="button">
              Cancel Edit
            </button>
          )}
        </form>

        {/* Manage Experiences */}
        <h2>Manage Experiences</h2>
        <div className="experiences-table-container">
          <table className="experiences-table">
            <thead>
              <tr>
                <th>Title</th>
                <th>Date</th>
                <th>Description</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {experiences.map((experience) => (
                <tr key={experience.id}>
                  <td>{experience.title}</td>
                  <td>{experience.date}</td>
                  <td>{experience.description}</td>
                  <td>
                    <button onClick={() => handleEditExperience(experience)}>Edit</button>
                    <button onClick={() => handleDelete(experience.id, 'experience')}>Delete</button>
