import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchProjects = createAsyncThunk(
  'projects/fetchProjects',
  async () => {
    const response = await axios.get('http://localhost:5000/projects');
    return response.data;
  }
);

export const addProject = createAsyncThunk(
  'projects/addProject',
  async (project) => {
    const response = await axios.post('http://localhost:5000/projects', project);
    return response.data;
  }
);

// Delete a project
export const deleteProject = createAsyncThunk(
  'projects/deleteProjects',
  async (id) => {
    await axios.delete(`http://localhost:5000/projects/${id}`);
    return id;
  }
);

// Update an existing project
export const updatePr = createAsyncThunk(
  'experiences/updateExperience',
  async ({ id, title, date, description, photo, link }) => {
    const response = await axios.put(`http://localhost:5000/experiences/${id}`, {
      title,
      date,
      description,
      photo,
      link
    });
    return response.data;
  }
);


