import { createSlice } from '@reduxjs/toolkit';

const experienceSlice = createSlice({
  name: 'experiences',
  initialState: [],
  reducers: {
    setExperiences: (state, action) => action.payload,
    addExperience: (state, action) => {
      state.push(action.payload);
    },
    deleteExperience: (state, action) => {
      return state.filter(experience => experience.id !== action.payload);
    },
    editExperience: (state, action) => {
      return state.map(experience => experience.id === action.payload.id ? action.payload : experience);
    }
  }
});

export const { setExperiences, addExperience, deleteExperience, editExperience } = experienceSlice.actions;
export default experienceSlice.reducer;
