import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './AdminItemList.css';

const AdminItemList = ({ itemType, fetchAction, deleteAction, editAction }) => {
  const dispatch = useDispatch();
  const items = useSelector((state) => state.admin[itemType]); // Ensure this matches the combined reducer

  useEffect(() => {
    dispatch(fetchAction());
  }, [dispatch, fetchAction]);

  const handleDelete = (id) => {
    if (deleteAction) {
      dispatch(deleteAction(id));
    }
  };

  const handleEdit = (item) => {
    // Implement the edit logic here
    dispatch(editAction(item));
  };

  return (
    <div className="admin-item-list">
      <h2>{itemType.charAt(0).toUpperCase() + itemType.slice(1)}</h2>
      <ul>
        {items.map((item) => (
          <li key={item.id}>
            <p>{item.title || item.description}</p>
            <button onClick={() => handleEdit(item)}>Edit</button>
            {deleteAction && <button onClick={() => handleDelete(item.id)}>Delete</button>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminItemList;
