import axios from 'axios';
import { createSlice } from '@reduxjs/toolkit';

const experienceSlice = createSlice({
  name: 'experiences',
  initialState: [],
  reducers: {
    setExperiences: (state, action) => action.payload,
    addExperience: (state, action) => {
      state.push(action.payload);
    }
  }
});

export const { setExperiences, addExperience } = experienceSlice.actions;

export const createExperience = (experience) => async (dispatch) => {
  const response = await axios.post('http://localhost:5000/experiences', experience);
  dispatch(addExperience(response.data));
};

export default experienceSlice.reducer;
