import axios from 'axios';
import { setSidebarInfo, setSidebarInfoError } from '../reducers/SidebarReducer';

export const fetchSidebarInfo = () => async (dispatch) => {
  try {
    const response = await axios.get('http://localhost:5000/sidebarInfo/1');
    dispatch(setSidebarInfo(response.data));
  } catch (error) {
    dispatch(setSidebarInfoError(error.message));
  }
};

export const updateSidebarInfo = (sidebarInfo) => async (dispatch) => {
  try {
    const response = await axios.put('http://localhost:5000/sidebarInfo/1', sidebarInfo);
    dispatch(setSidebarInfo(response.data));
  } catch (error) {
    dispatch(setSidebarInfoError(error.message));
  }
};
