import axios from 'axios';
import { createSlice } from '@reduxjs/toolkit';

const thoughtSlice = createSlice({
  name: 'thoughts',
  initialState: [],
  reducers: {
    setThoughts: (state, action) => action.payload,
    addThought: (state, action) => {
      state.push(action.payload);
    }
  }
});

export const { setThoughts, addThought } = thoughtSlice.actions;

export const createThought = (thought) => async (dispatch) => {
  const response = await axios.post('http://localhost:5000/thoughts', thought);
  dispatch(addThought(response.data));
};

export default thoughtSlice.reducer;
