import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <nav className="navbar">
      <div className="nav-links">
        <Link to="/" className="nav-link">Home</Link>
        <Link to="/projects" className="nav-link">Projects</Link>
        <Link to="/experience" className="nav-link">Experience</Link>
        <Link to="/admin" className="nav-link">Admin</Link>
      </div>
      <button className="menu-button" onClick={toggleMenu}>
        Menu
      </button>
      <div className={`nav-modal ${menuOpen ? 'active' : ''}`}>
        <Link to="/" className="nav-link" onClick={toggleMenu}>Home</Link>
        <Link to="/projects" className="nav-link" onClick={toggleMenu}>Projects</Link>
        <Link to="/experience" className="nav-link" onClick={toggleMenu}>Experience</Link>
        <Link to="/admin" className="nav-link" onClick={toggleMenu}>Admin</Link>
      </div>
    </nav>
  );
};

export default Navbar;
