import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchGeneralInfo } from '../actions/generalInfoActions';
import './Sidebar.css';

const Sidebar = () => {
  const dispatch = useDispatch();
  const generalInfo = useSelector((state) => state.generalInfo);

  useEffect(() => {
    dispatch(fetchGeneralInfo());
  }, [dispatch]);

  return (
    <aside className="sidebar">
      <h2>General Info</h2>
      {generalInfo.image && <img src={generalInfo.image} alt="General Info" className="general-info-image" />}
      <p>{generalInfo.description}</p>
    </aside>
  );
};

export default Sidebar;
