import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSidebarInfo } from '../actions/SidebarActions';
import './Sidebar.css';

const Sidebar = () => {
  const dispatch = useDispatch();
  const sidebarInfo = useSelector((state) => state.sidebarInfo);
  const error = useSelector((state) => state.sidebarInfoError);

  useEffect(() => {
    dispatch(fetchSidebarInfo());
  }, [dispatch]);

  if (error) {
    return <div className="sidebar"><h2>Error loading sidebar info</h2><p>{error}</p></div>;
  }

  return (
    <aside className="sidebar">
      <h2>General Info</h2>
      {sidebarInfo.image && <img src={sidebarInfo.image} alt="General Info" className="general-info-image" />}
      <h3>{sidebarInfo.fullName}</h3>
      <p>{sidebarInfo.description}</p>
      <h4>{sidebarInfo.currentOccupation}</h4>
    </aside>
  );
};

export default Sidebar;
