import React, { useState } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import MainContent from './components/MainContent';
import ThoughtsColumn from './components/ThoughtsColumn';
import MobileNav from './components/MobileNav';
import './App.css';

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [thoughtsOpen, setThoughtsOpen] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
    if (thoughtsOpen) setThoughtsOpen(false);
  };

  const toggleThoughts = () => {
    setThoughtsOpen(!thoughtsOpen);
    if (sidebarOpen) setSidebarOpen(false);
  };

  return (
    <Router>
      <div className="app">
        <Navbar />
        <MobileNav
          toggleSidebar={toggleSidebar}
          toggleThoughts={toggleThoughts}
          sidebarOpen={sidebarOpen}
          thoughtsOpen={thoughtsOpen}
        />
        <div className="content-wrapper">
          <Sidebar isOpen={sidebarOpen} />
          <MainContent />
          <ThoughtsColumn isOpen={thoughtsOpen} />
        </div>
      </div>
    </Router>
  );
}

export default App;
