import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addProject } from '../actions/projectActions';
import { addExperience } from '../actions/experienceActions';
import { addThought, fetchThoughts } from '../actions/thoughtActions'; // Import fetchThoughts
import './AdminPage.css';

const AdminPage = () => {
  const dispatch = useDispatch();
  const [project, setProject] = useState({ title: '', description: '', link: '' });
  const [experience, setExperience] = useState({ title: '', description: '', link: '' });
  const [thought, setThought] = useState({ text: '' });

  const handleProjectSubmit = (e) => {
    e.preventDefault();
    dispatch(addProject(project));
    setProject({ title: '', description: '', link: '' });
  };

  const handleExperienceSubmit = (e) => {
    e.preventDefault();
    dispatch(addExperience(experience));
    setExperience({ title: '', description: '', link: '' });
  };

  const handleThoughtSubmit = (e) => {
    e.preventDefault();
    dispatch(addThought(thought)).then(() => dispatch(fetchThoughts())); // Refetch thoughts after adding
    setThought({ text: '' });
  };

  return (
    <div className="admin-page">
      <h1>Admin Panel</h1>
      <form onSubmit={handleProjectSubmit}>
        <h2>Add Project</h2>
        <input
          type="text"
          placeholder="Project Title"
          value={project.title}
          onChange={(e) => setProject({ ...project, title: e.target.value })}
        />
        <textarea
          placeholder="Project Description"
          value={project.description}
          onChange={(e) => setProject({ ...project, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Project Link"
          value={project.link}
          onChange={(e) => setProject({ ...project, link: e.target.value })}
        />
        <button type="submit">Add Project</button>
      </form>
      <form onSubmit={handleExperienceSubmit}>
        <h2>Add Experience</h2>
        <input
          type="text"
          placeholder="Experience Title"
          value={experience.title}
          onChange={(e) => setExperience({ ...experience, title: e.target.value })}
        />
        <textarea
          placeholder="Experience Description"
          value={experience.description}
          onChange={(e) => setExperience({ ...experience, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Experience Link"
          value={experience.link}
          onChange={(e) => setExperience({ ...experience, link: e.target.value })}
        />
        <button type="submit">Add Experience</button>
      </form>
      <form onSubmit={handleThoughtSubmit}>
        <h2>Add Thought</h2>
        <textarea
          placeholder="Thought Text"
          value={thought.text}
          onChange={(e) => setThought({ ...thought, text: e.target.value })}
        />
        <button type="submit">Add Thought</button>
      </form>
    </div>
  );
};

export default AdminPage;
