import axios from 'axios';

// Action types
export const CREATE_THOUGHT = 'CREATE_THOUGHT';
export const FETCH_THOUGHTS = 'FETCH_THOUGHTS';
export const DELETE_THOUGHT = 'DELETE_THOUGHT';
export const EDIT_THOUGHT = 'EDIT_THOUGHT';

// Action creators
export const createThought = (thought) => async (dispatch) => {
  const response = await axios.post('http://localhost:5000/thoughts', thought);
  dispatch({ type: CREATE_THOUGHT, payload: response.data });
};

export const fetchThoughts = () => async (dispatch) => {
  const response = await axios.get('http://localhost:5000/thoughts');
  dispatch({ type: FETCH_THOUGHTS, payload: response.data });
};

export const deleteThought = (id) => async (dispatch) => {
  await axios.delete(`http://localhost:5000/thoughts/${id}`);
  dispatch({ type: DELETE_THOUGHT, payload: id });
};

export const editThought = (thought) => async (dispatch) => {
  const response = await axios.put(`http://localhost:5000/thoughts/${thought.id}`, thought);
  dispatch({ type: EDIT_THOUGHT, payload: response.data });
};
