import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSidebarInfo } from '../actions/SidebarActions'; // Updated import statement
import './Sidebar.css';

const Sidebar = () => {
  const dispatch = useDispatch();
  const sidebarInfo = useSelector((state) => state.sidebarInfo);

  useEffect(() => {
    dispatch(fetchSidebarInfo());
  }, [dispatch]);

  return (
    <aside className="sidebar">
      <h2>General Info</h2>
      {sidebarInfo.image && <img src={sidebarInfo.image} alt="General Info" className="general-info-image" />}
      <p>{sidebarInfo.description}</p>
    </aside>
  );
};

export default Sidebar;
