import axios from 'axios';
import { setSidebarInfo, setSidebarInfoError } from '../reducers/SidebarReducer';

export const fetchSidebarInfo = () => async (dispatch) => {
  try {
    const response = await axios.get('http://localhost:5000/sidebarInfo/1');
    dispatch(setSidebarInfo(response.data));
  } catch (error) {
    dispatch(setSidebarInfoError('Failed to fetch sidebar info'));
  }
};

export const updateSidebarInfo = (info) => async (dispatch) => {
  try {
    const response = await axios.put('http://localhost:5000/sidebarInfo/1', info);
    dispatch(setSidebarInfo(response.data));
  } catch (error) {
    dispatch(setSidebarInfoError('Failed to update sidebar info'));
  }
};
