import React from 'react';
import { useDispatch } from 'react-redux';
import { createProject } from '../actions/projectActions';
import { createExperience } from '../actions/experienceActions';
import { createThought } from '../actions/thoughtActions';
import './AdminPage.css';

const AdminPage = () => {
  const dispatch = useDispatch();

  return (
    <div className="admin-page">
      <h1>Admin Panel</h1>

      <form onSubmit={(e) => {
        e.preventDefault();
        const formData = new FormData(e.target);
        const project = {
          title: formData.get('title'),
          description: formData.get('description'),
          link: formData.get('link'),
          photos: Array.from(formData.getAll('photos')).map(file => URL.createObjectURL(file))
        };
        dispatch(createProject(project));
        e.target.reset();
      }}>
        <h2>Add Project</h2>
        <input type="text" name="title" placeholder="Project Title" required />
        <textarea name="description" placeholder="Project Description" required></textarea>
        <input type="text" name="link" placeholder="Project Link" />
        <input type="file" name="photos" multiple />
        <button type="submit">Add Project</button>
      </form>

      <form onSubmit={(e) => {
        e.preventDefault();
        const formData = new FormData(e.target);
        const experience = {
          description: formData.get('description'),
          link: formData.get('link'),
          photos: Array.from(formData.getAll('photos')).map(file => URL.createObjectURL(file))
        };
        dispatch(createExperience(experience));
        e.target.reset();
      }}>
        <h2>Add Experience</h2>
        <textarea name="description" placeholder="Experience Description" required></textarea>
        <input type="text" name="link" placeholder="Experience Link" />
        <input type="file" name="photos" multiple />
        <button type="submit">Add Experience</button>
      </form>

      <form onSubmit={(e) => {
        e.preventDefault();
        const formData = new FormData(e.target);
        const thought = {
          text: formData.get('text')
        };
        dispatch(createThought(thought));
        e.target.reset();
      }}>
        <h2>Add Thought</h2>
        <textarea name="text" placeholder="Your Thought" required></textarea>
        <button type="submit">Add Thought</button>
      </form>
    </div>
  );
};

export default AdminPage;
