import axios from 'axios';

// Action types
export const CREATE_EXPERIENCE = 'CREATE_EXPERIENCE';
export const FETCH_EXPERIENCES = 'FETCH_EXPERIENCES';
export const DELETE_EXPERIENCE = 'DELETE_EXPERIENCE';
export const EDIT_EXPERIENCE = 'EDIT_EXPERIENCE';

// Action creators
export const createExperience = (experience) => async (dispatch) => {
  const response = await axios.post('http://localhost:5000/experiences', experience);
  dispatch({ type: CREATE_EXPERIENCE, payload: response.data });
};

export const fetchExperiences = () => async (dispatch) => {
  const response = await axios.get('http://localhost:5000/experiences');
  dispatch({ type: FETCH_EXPERIENCES, payload: response.data });
};

export const deleteExperience = (id) => async (dispatch) => {
  await axios.delete(`http://localhost:5000/experiences/${id}`);
  dispatch({ type: DELETE_EXPERIENCE, payload: id });
};

export const editExperience = (experience) => async (dispatch) => {
  const response = await axios.put(`http://localhost:5000/experiences/${experience.id}`, experience);
  dispatch({ type: EDIT_EXPERIENCE, payload: response.data });
};
