import React from 'react';
import { useDispatch } from 'react-redux';
import {
  createProject,
  fetchProjects,
  deleteProject,
  editProject
} from '../actions/projectActions';
import {
  createExperience,
  fetchExperiences,
  deleteExperience,
  editExperience
} from '../actions/experienceActions';
import {
  createThought,
  fetchThoughts,
  deleteThought,
  editThought
} from '../actions/thoughtActions';
import {
  fetchSidebarInfo,
  updateSidebarInfo
} from '../actions/SidebarActions';
import AdminItemList from '../components/AdminItemList';
import './AdminPage.css';

const AdminPage = () => {
  const dispatch = useDispatch();

  return (
    <div className="admin-page">
      <h1>Admin Panel</h1>

      <form onSubmit={(e) => {
        e.preventDefault();
        dispatch(createProject({ title: 'New Project', description: 'Description' }));
      }}>
        <h2>Add Project</h2>
        <input type="text" placeholder="Project Title" />
        <textarea placeholder="Project Description"></textarea>
        <input type="text" placeholder="Project Link" />
        <input type="file" multiple />
        <button type="submit">Add Project</button>
      </form>

      <AdminItemList
        itemType="projects"
        fetchAction={fetchProjects}
        deleteAction={deleteProject}
        editAction={editProject}
      />

      <form onSubmit={(e) => {
        e.preventDefault();
        dispatch(createExperience({ description: 'New Experience', link: 'Link' }));
      }}>
        <h2>Add Experience</h2>
        <textarea placeholder="Experience Description"></textarea>
        <input type="text" placeholder="Experience Link" />
        <input type="file" />
        <button type="submit">Add Experience</button>
      </form>

      <AdminItemList
        itemType="experiences"
        fetchAction={fetchExperiences}
        deleteAction={deleteExperience}
        editAction={editExperience}
      />

      <form onSubmit={(e) => {
        e.preventDefault();
        dispatch(createThought({ text: 'New Thought' }));
      }}>
        <h2>Add Thought</h2>
        <textarea placeholder="Your Thought"></textarea>
        <button type="submit">Add Thought</button>
      </form>

      <AdminItemList
        itemType="thoughts"
        fetchAction={fetchThoughts}
        deleteAction={deleteThought}
        editAction={editThought}
      />

      <form onSubmit={(e) => {
        e.preventDefault();
        dispatch(updateSidebarInfo({
          fullName: 'Your Name',
          description: 'Your Info',
          currentOccupation: 'Your Occupation',
          image: 'path/to/image.jpg'
        }));
      }}>
        <h2>Edit General Info</h2>
        <input type="text" placeholder="Full Name" />
        <textarea placeholder="General Info Description"></textarea>
        <input type="text" placeholder="Current Occupation" />
        <input type="file" />
        <button type="submit">Update General Info</button>
      </form>

      <AdminItemList
        itemType="sidebarInfo"
        fetchAction={fetchSidebarInfo}
        editAction={updateSidebarInfo}
      />
    </div>
  );
};

export default AdminPage;
