import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchSidebarInfo = createAsyncThunk(
  'sidebar/fetchSidebarInfo',
  async () => {
    const response = await axios.get('/sidebarInfo'); // Note the relative path
    return response.data;
  }
);

export const updateSidebarInfo = createAsyncThunk(
  'sidebar/updateSidebarInfo',
  async (sidebarInfo) => {
    const response = await axios.put('/sidebarInfo', sidebarInfo); // Note the relative path
    return response.data;
  }
);
